import requests
import json
from bs4 import BeautifulSoup as bs

header = {
    'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/142.0.0.0 Safari/537.36'
}
payload = {
    'siteId': 's1',
    'template': 'catalog_section.84883e0558318bbac03e5c37b8bd1dfcb0a44cde4c80806644c98c23e94a10f6',
    'parameters': """.bc6d92350a5e2a99a1ed64cfd07127eb7a73c446cf71a466316e2b8cbe72dff7""",
    'action': 'showMore',
    'PAGEN_1': '1'
}
r = requests.post('https://kazan.zoloto55.ru/local/components/asmart/catalog.section/ajax.php', headers=header, data=payload)
data = bs(r.content, 'lxml')
items = data.findAll('div', class_='catalog-product-wrap')
for item in items:
    print(item.find(class_='catalog-product-title').text)
    print(f"https://kazan.zoloto55.ru{item.find(class_='catalog-product-title').get('href')}")
    print(f"Image: https://kazan.zoloto55.ru{item.find('img', class_='product_item_img').get('src')}")
    print(f"Price: {item.find('div', class_='catalog-product-price-now').text.strip()}")
    print('_'*20)